import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  font-family: Arial, sans-serif;
`;

const TimerDisplay = styled.div`
  font-size: 6rem;
  font-weight: bold;
  margin: 2rem 0;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
`;

const Milliseconds = styled.span`
  font-size: 3rem;
  opacity: 0.8;
`;

const Button = styled.button`
  padding: 1rem 2rem;
  font-size: 1.2rem;
  border: none;
  border-radius: 50px;
  cursor: pointer;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;
  margin: 0 0.5rem;
  color: white;
  font-weight: bold;

  ${props => props.variant === 'start' && `
    background: #4CAF50;
    &:hover {
      background: #45a049;
    }
  `}

  ${props => props.variant === 'pause' && `
    background: #FFC107;
    &:hover {
      background: #FFB300;
    }
  `}

  ${props => props.variant === 'reset' && `
    background: #f44336;
    &:hover {
      background: #e53935;
    }
  `}

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 8px rgba(0, 0, 0, 0.2);
  }

  &:active {
    transform: translateY(0);
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    transform: none;
  }
`;

function App() {
  const [time, setTime] = useState(0);
  const [isRunning, setIsRunning] = useState(false);

  useEffect(() => {
    let intervalId;
    if (isRunning) {
      intervalId = setInterval(() => {
        setTime(prevTime => prevTime + 1);
      }, 10);
    }
    return () => clearInterval(intervalId);
  }, [isRunning]);

  const startTimer = () => {
    setIsRunning(true);
  };

  const pauseTimer = () => {
    setIsRunning(false);
  };

  const resetTimer = () => {
    setTime(0);
    setIsRunning(false);
  };

  const formatTime = (centiseconds) => {
    const hours = Math.floor(centiseconds / 360000);
    const minutes = Math.floor((centiseconds % 360000) / 6000);
    const seconds = Math.floor((centiseconds % 6000) / 100);
    const remainingCentiseconds = centiseconds % 100;

    const mainTime = `${String(hours).padStart(2, '0')}:${String(minutes).padStart(2, '0')}:${String(seconds).padStart(2, '0')}`;
    const fraction = String(remainingCentiseconds).padStart(2, '0');

    return { mainTime, fraction };
  };

  const { mainTime, fraction } = formatTime(time);

  return (
    <Container>
      <TimerDisplay>
        {mainTime}
        <Milliseconds>.{fraction}</Milliseconds>
      </TimerDisplay>
      <div>
        <Button variant="start" onClick={startTimer} disabled={isRunning}>
          开始
        </Button>
        <Button variant="pause" onClick={pauseTimer} disabled={!isRunning}>
          暂停
        </Button>
        <Button variant="reset" onClick={resetTimer}>
          重置
        </Button>
      </div>
    </Container>
  );
}

export default App;
